import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { registerAIRoutes } from "./ai-routes";
import intelligentAgentRoutes from "./routes/intelligent-agent";
import apiKeyRoutes from "./routes/api-key-management";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json({ limit: '50mb' }));
app.use(express.urlencoded({ limit: '50mb', extended: true }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);
  registerAIRoutes(app);

  // Register intelligent agent routes
  app.use('/api', intelligentAgentRoutes);

  // Register API key management routes
  app.use('/api', apiKeyRoutes);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertProductSchema, insertStockSchema, insertSaleSchema, insertCaptureSchema, insertDocumentSchema, insertUserLearningPatternSchema } from "@shared/schema";
import { z } from "zod";
import aiRoutes from "./routes/ai";
import multer from 'multer';

export async function registerRoutes(app: Express): Promise<Server> {
  // Enhanced comprehensive logging middleware for debug visibility in Replit console
  app.use((req, res, next) => {
    const timestamp = new Date().toISOString();
    console.log(`\n=== [${timestamp}] ${req.method} ${req.path} ===`);

    if (req.query && Object.keys(req.query).length > 0) {
      console.log(`[QUERY PARAMS]`, JSON.stringify(req.query, null, 2));
    }

    if (req.body && Object.keys(req.body).length > 0) {
      console.log(`[REQUEST BODY]`, JSON.stringify(req.body, null, 2));
    }

    if (req.headers.authorization) {
      console.log(`[AUTH]`, 'Bearer token present');
    }

    // Log session info if available  
    if ((req as any).session?.userId) {
      console.log(`[SESSION]`, { userId: (req as any).session.userId, userRole: (req as any).session.userRole });
    }

    // Intercept response to log status and response body
    const originalSend = res.send;
    res.send = function(body) {
      console.log(`[RESPONSE ${res.statusCode}]`, typeof body === 'string' ? body.substring(0, 200) + (body.length > 200 ? '...' : '') : body);
      console.log(`=== END ${req.method} ${req.path} ===\n`);
      return originalSend.call(this, body);
    };

    next();
  });

  // AI routes
  app.use("/api/ai", aiRoutes);

  // Document storage setup
  const documentUpload = multer({ 
    storage: multer.memoryStorage(),
    limits: { fileSize: 10 * 1024 * 1024 } // 10MB limit
  });

  // Document storage routes
  app.post("/api/documents", documentUpload.single('image'), async (req: any, res) => {
    try {
      console.log('[SERVER-DEBUG] Document storage request received', {
        hasFile: !!req.file,
        fileName: req.file?.originalname,
        fileSize: req.file?.size,
        fileMimeType: req.file?.mimetype,
        bodyKeys: Object.keys(req.body || {}),
        contentType: req.headers['content-type'],
        userAgent: req.headers['user-agent']?.substring(0, 100),
        origin: req.headers.origin
      });

      if (!req.file) {
        console.error('[SERVER-DEBUG] No file uploaded - FormData issue');
        return res.status(400).json({ 
          error: 'No image file provided',
          details: 'The image field is missing from FormData. Check client upload.'
        });
      }

      const { confirmedType, extractedData, userRole, deviceType } = req.body;

      console.log('[SERVER-DEBUG] Request body parsed', {
        confirmedType,
        userRole,
        deviceType,
        hasExtractedData: !!extractedData,
        extractedDataLength: extractedData?.length
      });

      if (!confirmedType) {
        console.error('[SERVER-DEBUG] Missing confirmedType field');
        return res.status(400).json({ 
          error: 'Document type confirmation required',
          details: 'The confirmedType field is missing from request'
        });
      }

      let parsedExtractedData;
      try {
        parsedExtractedData = JSON.parse(extractedData || '{}');
        console.log('[SERVER-DEBUG] ExtractedData parsed successfully', {
          keys: Object.keys(parsedExtractedData),
          hasLineItems: !!parsedExtractedData.extractedData?.lineItems,
          itemCount: parsedExtractedData.extractedData?.lineItems?.length || 0
        });
      } catch (parseError) {
        console.error('[SERVER-DEBUG] Failed to parse extractedData:', parseError);
        return res.status(400).json({ 
          error: 'Invalid extracted data format',
          details: parseError instanceof Error ? parseError.message : 'JSON parse failed'
        });
      }

      // Role metadata for compliance and audit requirements
      const roleMetadata = {
        userRole: userRole || 'retailer',
        timestamp: new Date().toISOString(),
        complianceFlags: {
          auditRequired: userRole === 'wholesaler',
          regulatoryTracking: true,
          businessClassification: userRole || 'retailer'
        }
      };

      // Generate business intelligence and compliance metadata
      const businessIntelligence = generateBusinessIntelligence(parsedExtractedData);
      const complianceMetadata = generateComplianceMetadata(parsedExtractedData, req.file.originalname);

      // Store document with full-fidelity compliance data + business intelligence
      const documentData = {
        enterpriseId: 'default',
        userId: 'user-1', // TODO: Get from session
        fileName: req.file.originalname,
        docType: parsedExtractedData.documentType || 'other',
        confirmedType: confirmedType,
        confidence: parsedExtractedData.confidence || 0.5,
        rawText: parsedExtractedData.rawText || '',
        modelSummary: parsedExtractedData.modelSummary || '',
        header: parsedExtractedData.extractedData?.header || {},
        lineItems: parsedExtractedData.extractedData?.lineItems || [],
        totals: parsedExtractedData.extractedData?.totals || {},
        extractedData: parsedExtractedData.extractedData || {},
        // ENHANCED: Store business entities separately
        businessEntities: parsedExtractedData.extractedData?.businessEntities || [],
        // COMPLIANCE: Preserve complete original OCR JSON for audit trail
        originalOcrJson: parsedExtractedData,
        // BUSINESS INTELLIGENCE: Add classification and analysis
        businessIntelligence: businessIntelligence,
        complianceMetadata: complianceMetadata,
        // ROLE METADATA: Store user role for compliance and audit
        roleMetadata: roleMetadata,
        // Enhanced searchable fields with ALL GSTINs
        allGstns: extractAllGstns(parsedExtractedData),
        searchableText: generateSearchableText(parsedExtractedData),
        fileUrl: `uploads/${req.file.originalname}`,
        processingTime: parsedExtractedData.metadata?.processingTime || 0,
        ocrDurationMs: parsedExtractedData.metadata?.ocrDurationMs || 0,
        mongoWriteMs: 0, // Will be calculated
        tags: businessIntelligence.suggestedTags || []
      };

      documentData.mongoWriteMs = Date.now() - documentData.ocrDurationMs;

      console.log('[DOCUMENTS] Storing document:', documentData.fileName);
      console.log('[DOCUMENTS-DEBUG] Confirmed type:', confirmedType);
      console.log('[DOCUMENTS-DEBUG] Raw extractedData string:', extractedData?.substring(0, 200));
      console.log('[DOCUMENTS-DEBUG] Parsed data keys:', Object.keys(parsedExtractedData));
      console.log('[DOCUMENTS-DEBUG] Raw text from parsed:', parsedExtractedData.rawText?.length || 0);
      console.log('[DOCUMENTS-DEBUG] Line items from parsed:', parsedExtractedData.extractedData?.lineItems?.length || 0);
      console.log('[DOCUMENTS] Raw text length:', documentData.rawText?.length || 0);
      console.log('[DOCUMENTS] Line items count:', documentData.lineItems?.length || 0);

      // Store document with real persistence 
      const documentId = `doc-${Date.now()}`;
      const result = {
        id: documentId,
        ...documentData,
        createdAt: new Date().toISOString()
      };

      // Add to in-memory storage
      documentStorage.push(result);

      console.log('[DOCUMENTS-DEBUG] Current document storage size:', documentStorage.length);
      console.log('[DOCUMENTS-DEBUG] Document stored with ID:', result.id);

      // Store learning pattern for AI improvement
      if (confirmedType !== parsedExtractedData.documentType) {
        console.log('[LEARNING] User corrected AI prediction:', {
          ai: parsedExtractedData.documentType,
          user: confirmedType
        });
      }

      console.log('[DOCUMENTS] Stored successfully with', result.lineItems?.length || 0, 'line items');
      res.json(result);

    } catch (error) {
      console.error('[DOCUMENTS] Storage failed:', error);
      res.status(500).json({ error: 'Failed to store document' });
    }
  });

  // Business Intelligence Generator
  function generateBusinessIntelligence(ocrData: any) {
    const header = ocrData.extractedData?.header || {};
    const lineItems = ocrData.extractedData?.lineItems || [];
    const totals = ocrData.extractedData?.totals || {};

    // Classify business relationship
    let businessRelationship = 'unknown';
    let entityTypes = { supplier: 'unknown', buyer: 'unknown' };

    // Analyze business entities based on name patterns
    const supplierName = header.supplier?.toLowerCase() || '';
    const buyerName = header.buyer?.toLowerCase() || '';

    // Supplier classification
    if (supplierName.includes('medical agencies') || supplierName.includes('pharmaceuticals') || supplierName.includes('pharma')) {
      entityTypes.supplier = supplierName.includes('agencies') ? 'wholesaler' : 'manufacturer';
    } else if (supplierName.includes('medical') || supplierName.includes('pharmacy')) {
      entityTypes.supplier = 'retailer';
    }

    // Buyer classification  
    if (buyerName.includes('medical') || buyerName.includes('pharmacy')) {
      entityTypes.buyer = buyerName.includes('medicals') ? 'retailer' : 'pharmacy';
    }

    // Determine relationship
    if (entityTypes.supplier === 'wholesaler' && entityTypes.buyer === 'retailer') {
      businessRelationship = 'wholesaler_to_retailer';
    } else if (entityTypes.supplier === 'manufacturer' && entityTypes.buyer === 'wholesaler') {
      businessRelationship = 'manufacturer_to_wholesaler';
    }

    // Calculate business metrics
    const totalValue = parseFloat(totals.net) || 0;
    const itemCount = lineItems.length;
    const avgItemValue = itemCount > 0 ? totalValue / itemCount : 0;

    // Medicine categories
    const medicineCategories = lineItems.map((item: any) => {
      const name = item.name?.toLowerCase() || '';
      if (name.includes('tab') || name.includes('tablet')) return 'tablet';
      if (name.includes('syrup') || name.includes('liquid')) return 'liquid';
      if (name.includes('gel') || name.includes('cream')) return 'topical';
      if (name.includes('injection')) return 'injection';
      return 'other';
    });

    const categoryCount = medicineCategories.reduce((acc: any, cat: string) => {
      acc[cat] = (acc[cat] || 0) + 1;
      return acc;
    }, {});

    return {
      businessRelationship,
      entityTypes,
      transactionMetrics: {
        totalValue,
        itemCount,
        avgItemValue,
        medicineCategories: categoryCount
      },
      riskProfile: totalValue > 10000 ? 'high_value' : totalValue > 5000 ? 'medium_value' : 'standard',
      suggestedTags: [
        businessRelationship,
        entityTypes.supplier,
        entityTypes.buyer,
        totalValue > 10000 ? 'high_value' : 'standard_value'
      ].filter(Boolean)
    };
  }

  function generateComplianceMetadata(ocrData: any, fileName: string) {
    return {
      auditTrail: {
        uploadTimestamp: new Date().toISOString(),
        fileName,
        ocrVersion: 'gpt-4o-mini',
        extractionTimestamp: new Date().toISOString()
      },
      regulatoryData: {
        documentType: ocrData.documentType,
        businessEntities: {
          supplier: ocrData.extractedData?.header?.supplier,
          buyer: ocrData.extractedData?.header?.buyer,
          gstin: ocrData.extractedData?.header?.gstin
        },
        medicineCount: ocrData.extractedData?.lineItems?.length || 0,
        totalValue: ocrData.extractedData?.totals?.net,
        taxDetails: {
          cgst: ocrData.extractedData?.totals?.cgst,
          sgst: ocrData.extractedData?.totals?.sgst,
          igst: ocrData.extractedData?.totals?.igst
        }
      },
      dataIntegrity: {
        originalTextLength: ocrData.rawText?.length || 0,
        extractedFields: Object.keys(ocrData.extractedData || {}).length,
        confidence: ocrData.confidence
      }
    };
  }

  function extractAllGstns(ocrData: any): string[] {
    const gstns: Set<string> = new Set();

    // From enhanced header fields (supplier and buyer GSTNs)
    if (ocrData.extractedData?.header?.supplierGstn) {
      gstns.add(ocrData.extractedData.header.supplierGstn);
    }
    if (ocrData.extractedData?.header?.buyerGstn) {
      gstns.add(ocrData.extractedData.header.buyerGstn);
    }
    // Legacy single gstn field for backward compatibility
    if (ocrData.extractedData?.header?.gstn) {
      gstns.add(ocrData.extractedData.header.gstn);
    }

    // From business entities array
    if (ocrData.extractedData?.businessEntities) {
      ocrData.extractedData.businessEntities.forEach((entity: any) => {
        if (entity.gstn) gstns.add(entity.gstn);
      });
    }

    // From allGstns array if provided by OCR
    if (ocrData.extractedData?.allGstns) {
      ocrData.extractedData.allGstns.forEach((gstn: string) => {
        if (gstn && gstn.length === 15) gstns.add(gstn);
      });
    }

    // From raw text - comprehensive GSTN pattern matching
    const rawText = ocrData.rawText || '';

    // Standard GSTN format: 2 digits + 10 characters + 1 digit + 2 characters
    const gstnPattern = /([0-9]{2}[A-Z]{5}[0-9]{4}[A-Z][1-9A-Z][Z][0-9A-Z])/g;
    const matches = rawText.match(gstnPattern);

    if (matches) {
      matches.forEach((match: string) => gstns.add(match));
    }

    // Look for GSTN labels and extract the number after
    const gstPattern = /GSTN?\s*:?\s*([0-9A-Z]{15})/gi;
    const gstMatches = rawText.match(gstPattern);

    if (gstMatches) {
      gstMatches.forEach((match: string) => {
        const gstn = match.replace(/GSTN?\s*:?\s*/i, '').trim();
        if (gstn.length === 15) gstns.add(gstn);
      });
    }

    // Additional patterns for OCR variations
    const gstVariations = [
      /GST\s*NO\.?\s*:?\s*([0-9A-Z]{15})/gi,
      /GST\s*NUMBER\s*:?\s*([0-9A-Z]{15})/gi,
      /TIN\s*:?\s*([0-9A-Z]{15})/gi
    ];

    gstVariations.forEach(pattern => {
      const variationMatches = rawText.match(pattern);
      if (variationMatches) {
        variationMatches.forEach((match: string) => {
          const gstn = match.replace(/.*:\s*/, '').trim();
          if (gstn.length === 15) gstns.add(gstn);
        });
      }
    });

    console.log('[GSTN-EXTRACTION] Input OCR data has header:', JSON.stringify(ocrData.extractedData?.header));
    console.log('[GSTN-EXTRACTION] Input OCR data has businessEntities:', JSON.stringify(ocrData.extractedData?.businessEntities));
    console.log('[GSTN-EXTRACTION] Input OCR data has allGstns:', JSON.stringify(ocrData.extractedData?.allGstns));
    console.log('[GSTN-EXTRACTION] Raw text GSTN search results:', rawText.match(/([0-9]{2}[A-Z]{5}[0-9]{4}[A-Z][1-9A-Z][Z][0-9A-Z])/g));
    console.log('[GSTN-EXTRACTION] Final extracted GSTNs:', Array.from(gstns));
    return Array.from(gstns);
  }

  function generateSearchableText(ocrData: any): string {
    const parts = [
      ocrData.rawText || '',
      ocrData.extractedData?.header?.supplier || '',
      ocrData.extractedData?.header?.buyer || '',
      ocrData.extractedData?.header?.gstn || '',
      ocrData.extractedData?.header?.docNo || '',
      ...(ocrData.extractedData?.lineItems?.map((item: any) => item.name) || [])
    ];

    return parts.filter(Boolean).join(' ').toLowerCase();
  }

  // In-memory document storage for now (should be database in production)
  const documentStorage: Array<any> = [];

  app.get("/api/documents", async (req, res) => {
    try {
      console.log('[DOCUMENTS] Fetching documents:', documentStorage.length);
      res.json(documentStorage);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch documents" });
    }
  });

  // POST endpoint for direct document storage (used by confirmation flow)
  app.post("/api/documents", documentUpload.single('image'), async (req, res) => {
    try {
      console.log('[DOCUMENTS-POST] Processing document storage request');

      if (!req.file) {
        return res.status(400).json({ error: 'No image file provided' });
      }

      const { confirmedType, extractedData: extractedDataString, userRole } = req.body;

      if (!confirmedType) {
        return res.status(400).json({ error: 'Confirmed document type is required' });
      }

      let parsedExtractedData;
      try {
        parsedExtractedData = JSON.parse(extractedDataString || '{}');
      } catch (e) {
        console.error('[DOCUMENTS-POST] Failed to parse extracted data:', e);
        return res.status(400).json({ error: 'Invalid extracted data format' });
      }

      console.log('[DOCUMENTS-POST] Request data:', {
        fileName: req.file.originalname,
        confirmedType,
        userRole,
        extractedDataKeys: Object.keys(parsedExtractedData)
      });

      // Generate business intelligence
      const businessIntelligence = generateBusinessIntelligence(parsedExtractedData);

      const documentData = {
        fileName: req.file.originalname,
        fileSize: req.file.size,
        confirmedType,
        userRole: userRole || 'retailer',
        rawText: parsedExtractedData.rawText || '',
        extractedData: parsedExtractedData.extractedData || {},
        header: parsedExtractedData.extractedData?.header || {},
        lineItems: parsedExtractedData.extractedData?.lineItems || [],
        totals: parsedExtractedData.extractedData?.totals || {},
        businessIntelligence,
        allGstns: extractAllGstns(parsedExtractedData),
        searchableText: generateSearchableText(parsedExtractedData),
        fileUrl: `uploads/${req.file.originalname}`,
        processingTime: 0,
        ocrDurationMs: 0,
        mongoWriteMs: 0,
        tags: businessIntelligence.suggestedTags || []
      };

      const documentId = `doc-${Date.now()}`;
      const result = {
        id: documentId,
        ...documentData,
        createdAt: new Date().toISOString()
      };

      // Add to in-memory storage
      documentStorage.push(result);

      console.log('[DOCUMENTS-POST] Document stored successfully with ID:', documentId);
      console.log('[DOCUMENTS-POST] Current document storage size:', documentStorage.length);

      res.json(result);

    } catch (error) {
      console.error('[DOCUMENTS-POST] Storage failed:', error);
      res.status(500).json({ error: 'Failed to store document' });
    }
  });

  // Products routes
  app.get("/api/products", async (req, res) => {
    try {
      console.log('[API] Fetching products...');
      const products = await storage.getProducts();
      console.log(`[API] Found ${products.length} products`);
      res.json(products);
    } catch (error) {
      console.error('[API ERROR] Failed to fetch products:', error);
      res.status(500).json({ error: "Failed to fetch products" });
    }
  });

  app.post("/api/products", async (req, res) => {
    try {
      const productData = insertProductSchema.parse(req.body);
      const product = await storage.createProduct(productData);
      res.status(201).json(product);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ error: "Invalid product data", details: error.errors });
      } else {
        res.status(500).json({ error: "Failed to create product" });
      }
    }
  });

  app.get("/api/products/:id", async (req, res) => {
    try {
      const product = await storage.getProduct(req.params.id);
      if (!product) {
        return res.status(404).json({ error: "Product not found" });
      }
      res.json(product);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch product" });
    }
  });

  // Stock routes
  app.get("/api/stock", async (req, res) => {
    try {
      const stock = await storage.getStock();
      res.json(stock);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch stock" });
    }
  });

  app.post("/api/stock", async (req, res) => {
    try {
      const stockData = insertStockSchema.parse(req.body);
      const stock = await storage.createStock(stockData);
      res.status(201).json(stock);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ error: "Invalid stock data", details: error.errors });
      } else {
        res.status(500).json({ error: "Failed to create stock" });
      }
    }
  });

  app.get("/api/stock/product/:productId", async (req, res) => {
    try {
      const stock = await storage.getStockByProduct(req.params.productId);
      res.json(stock);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch stock" });
    }
  });

  // Barcode lookup route
  app.post("/api/barcode/lookup", async (req, res) => {
    try {
      const { barcode } = req.body;

      // Mock barcode lookup - in production this would query a drug database
      const mockProductMapping: Record<string, string> = {
        '1234567890123': 'prod-1', // Paracetamol
        '9876543210987': 'prod-2', // Cough Syrup  
        '1111222233334': 'prod-1', // Another barcode for Paracetamol
      };

      const productId = mockProductMapping[barcode];

      if (productId) {
        // Get product details
        const products = await storage.getProducts();
        const product = products.find(p => p.id === productId);

        if (product) {
          res.json({
            barcode,
            productId,
            product,
            found: true
          });
        } else {
          res.json({
            barcode,
            found: false,
            message: 'Product not found in inventory'
          });
        }
      } else {
        res.json({
          barcode,
          found: false,
          message: 'Barcode not recognized'
        });
      }
    } catch (error) {
      res.status(500).json({ error: 'Barcode lookup failed' });
    }
  });

  // Sales routes
  app.get("/api/sales", async (req, res) => {
    try {
      const sales = await storage.getSales();
      res.json(sales);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch sales" });
    }
  });

  app.post("/api/sales", async (req, res) => {
    try {
      const saleData = insertSaleSchema.parse(req.body);
      const sale = await storage.createSale(saleData);

      // Create outbox entry for offline sync
      await storage.createOutboxItem({
        tableName: 'sales',
        rowId: sale.id,
        operation: 'create',
        payload: JSON.stringify(sale)
      });

      res.status(201).json(sale);
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({ error: "Invalid sale data", details: error.errors });
      } else {
        res.status(500).json({ error: "Failed to create sale" });
      }
    }
  });

  app.get("/api/sales/today", async (req, res) => {
    try {
      const todaysSales = await storage.getTodaysSales();
      res.json({ total: todaysSales });
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch today's sales" });
    }
  });

  // Stock bulk insert endpoint for OCR processing
  app.post('/api/stock/bulk', async (req, res) => {
    try {
      console.log('[STOCK-BULK] Processing bulk stock insert');
      console.log('[STOCK-BULK] Request body:', JSON.stringify(req.body, null, 2));

      const items = req.body.items as Array<{
        name: string;
        batch: string;
        qty: number;
        exp: string;
        mrp: number;
      }>;

      const { billNumber, date } = req.body;

      if (!items || !Array.isArray(items)) {
        throw new Error('Items array is required');
      }

      // Log duplicate check info (actual duplicate prevention done on frontend with document storage)
      if (billNumber && date) {
        console.log(`[STOCK-BULK] Processing invoice: ${billNumber} dated ${date}`);
      }

      console.log('[STOCK-BULK] Items to process:', items.length);

      for (const item of items) {
        // Find or create product
        const products = await storage.getProducts();
        let product = products.find(p => p.name === item.name);

        if (!product) {
          const productData = {
            name: item.name,
            description: `Added from OCR scan`,
            price: item.mrp
          };

          product = await storage.createProduct(productData);
          console.log('[STOCK-BULK] Created new product:', product.name);
        }

        // Create stock entry
        const stockData = {
          productId: product.id,
          batchNumber: item.batch,
          quantity: item.qty,
          expiryDate: new Date(item.exp)
        };

        await storage.createStock(stockData);
        console.log('[STOCK-BULK] Added stock:', item.name, 'qty:', item.qty);
      }

      console.log('[STOCK-BULK] Completed successfully - items processed:', items.length);
      res.status(201).json({ 
        success: true, 
        itemsProcessed: items.length,
        message: `${items.length} items added to stock from OCR scan`
      });

    } catch (error) {
      console.error('[STOCK-BULK] Error:', error);
      res.status(500).json({ error: 'Failed to process bulk stock insert' });
    }
  });

  // Captures API
  app.post("/api/captures", async (req, res) => {
    try {
      console.log('[API] Creating capture with data:', req.body);

      // Basic capture logging (storage method will be added later)
      const captureData = {
        id: `capture-${Date.now()}`,
        ...req.body,
        createdAt: new Date().toISOString()
      };

      console.log('[API] Capture logged successfully:', captureData.id);
      res.status(201).json(captureData);
    } catch (error) {
      console.error('[API ERROR] Failed to create capture:', error);
      res.status(500).json({ error: "Failed to create capture" });
    }
  });

  app.get("/api/captures", async (req, res) => {
    try {
      console.log('[API] Fetching captures...');
      res.json([]);
    } catch (error) {
      console.error('[API ERROR] Failed to fetch captures:', error);
      res.status(500).json({ error: "Failed to fetch captures" });
    }
  });

  // Sync routes
  app.post("/api/sync/batch", async (req, res) => {
    try {
      const unsyncedItems = await storage.getUnsyncedOutboxItems();

      // Process each unsynced item idempotently
      for (const item of unsyncedItems) {
        // Mark as synced (in real implementation, this would sync to remote)
        await storage.markOutboxItemSynced(item.id);
      }

      res.json({ 
        processed: unsyncedItems.length,
        message: "Batch sync completed successfully"
      });
    } catch (error) {
      res.status(500).json({ error: "Failed to process sync batch" });
    }
  });

  app.get("/api/sync/status", async (req, res) => {
    try {
      const unsyncedItems = await storage.getUnsyncedOutboxItems();
      res.json({
        pendingItems: unsyncedItems.length,
        lastSync: new Date().toISOString(),
        online: true
      });
    } catch (error) {
      res.status(500).json({ error: "Failed to get sync status" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

