import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { enhancedCapture } from '@/services/enhanced-capture';
import { documentStorage, type StoredDocument } from '@/services/document-storage';
import { remoteAI } from '@/lib/ai-assistant';
import { createModuleLogger } from '@/utils/app-logger';
import { useToast } from '@/hooks/use-toast';
import { BarChart3, FileText, Calendar, Search, Sparkles, Download, Trash2 } from 'lucide-react';

const log = createModuleLogger('Reports');

interface DocumentStats {
  totalDocuments: number;
  documentsByType: Record<string, number>;
  unsyncedCount: number;
  totalSize: number;
}

interface AIReport {
  id: string;
  title: string;
  content: string;
  timestamp: Date;
  type: 'summary' | 'analysis' | 'insights';
}

export default function ReportsScreen() {
  const [documents, setDocuments] = useState<StoredDocument[]>([]);
  const [stats, setStats] = useState<DocumentStats>({ 
    totalDocuments: 0, 
    documentsByType: {}, 
    unsyncedCount: 0, 
    totalSize: 0 
  });
  const [selectedType, setSelectedType] = useState<string>('all');
  const [searchText, setSearchText] = useState<string>('');
  const [isGeneratingReport, setIsGeneratingReport] = useState(false);
  const [aiReports, setAiReports] = useState<AIReport[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  const { toast } = useToast();

  useEffect(() => {
    loadDocumentsAndStats();
  }, [selectedType, searchText]);

  const loadDocumentsAndStats = async () => {
    try {
      setIsLoading(true);
      log.info('Loading documents and stats', { selectedType, searchText });

      // Load documents based on filters
      const filter = {
        type: selectedType === 'all' ? undefined : selectedType as any,
        searchText: searchText || undefined
      };

      const [docsData, statsData] = await Promise.all([
        documentStorage.searchDocuments(filter),
        documentStorage.getStorageStats()
      ]);

      setDocuments(docsData);
      setStats(statsData);

      log.info('Documents and stats loaded', { 
        documentsCount: docsData.length, 
        totalSize: statsData.totalSize 
      });

    } catch (error) {
      log.error('Failed to load documents and stats', error as Error);
      toast({
        title: 'Loading Error',
        description: 'Failed to load document data',
        variant: 'destructive'
      });
    } finally {
      setIsLoading(false);
    }
  };

  const generateAIReport = async (reportType: 'summary' | 'analysis' | 'insights') => {
    try {
      setIsGeneratingReport(true);
      log.info('Generating AI report', { reportType });

      // Get recent documents for context
      const recentDocs = await enhancedCapture.getRecentDocuments(20);

      // Prepare data for AI analysis
      const documentSummary = recentDocs.map(doc => ({
        type: doc.type,
        date: doc.createdAt,
        medicines: doc.analysis.extractedData.medicines?.map(m => m.name) || [],
        total: doc.analysis.extractedData.total,
        summary: doc.analysis.rawText.substring(0, 300)
      }));

      let prompt = '';
      let reportTitle = '';

      switch (reportType) {
        case 'summary':
          prompt = `Generate a comprehensive summary report for our pharmacy document analysis system. 

            Here's the data from ${documentSummary.length} recent documents:
            ${JSON.stringify(documentSummary, null, 2)}

            Please provide:
            1. Overall document processing summary
            2. Most common document types and patterns
            3. Key medicines and trends identified
            4. Total transactions processed
            5. Notable insights or recommendations

            Format as a professional pharmacy management report.`;
          reportTitle = 'Document Processing Summary';
          break;

        case 'analysis':
          prompt = `Analyze the pharmacy document data for business insights:

            Document Data: ${JSON.stringify(documentSummary, null, 2)}

            Provide analysis on:
            1. Medicine popularity and demand patterns
            2. Revenue trends from bills and invoices
            3. Prescription patterns and compliance
            4. Inventory recommendations based on document patterns
            5. Operational efficiency insights

            Focus on actionable business intelligence.`;
          reportTitle = 'Business Intelligence Analysis';
          break;

        case 'insights':
          prompt = `Generate strategic insights from our pharmacy document processing:

            Data: ${JSON.stringify(documentSummary, null, 2)}

            Provide insights on:
            1. Market trends visible in the processed documents
            2. Customer behavior patterns
            3. Seasonal or temporal patterns in medicine demands
            4. Recommendations for inventory optimization
            5. Process improvements for document handling

            Think like a pharmacy business consultant.`;
          reportTitle = 'Strategic Insights & Recommendations';
          break;
      }

      const aiResponse = await remoteAI.ask(prompt, {
        currentScreen: 'Reports',
        recentActions: ['Generate AI Report']
      });

      const newReport: AIReport = {
        id: crypto.randomUUID(),
        title: reportTitle,
        content: aiResponse,
        timestamp: new Date(),
        type: reportType
      };

      setAiReports(prev => [newReport, ...prev]);

      toast({
        title: 'Report Generated',
        description: `${reportTitle} created successfully`,
        variant: 'default'
      });

      log.info('AI report generated successfully', { reportType, reportId: newReport.id });

    } catch (error) {
      log.error('Failed to generate AI report', error as Error);
      toast({
        title: 'Report Generation Failed',
        description: 'Could not generate AI report. Please try again.',
        variant: 'destructive'
      });
    } finally {
      setIsGeneratingReport(false);
    }
  };

  const deleteDocument = async (documentId: string) => {
    try {
      log.info('Deleting document', { documentId });

      const success = await enhancedCapture.deleteDocument(documentId);

      if (success) {
        setDocuments(prev => prev.filter(doc => doc.id !== documentId));
        toast({
          title: 'Document Deleted',
          description: 'Document removed successfully',
          variant: 'default'
        });

        // Refresh stats
        loadDocumentsAndStats();
      } else {
        throw new Error('Delete operation failed');
      }
    } catch (error) {
      log.error('Failed to delete document', error as Error);
      toast({
        title: 'Delete Failed',
        description: 'Could not delete document',
        variant: 'destructive'
      });
    }
  };

  const formatFileSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const formatDate = (date: Date): string => {
    return new Intl.DateTimeFormat('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    }).format(date);
  };

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'bill': return 'ðŸ§¾';
      case 'prescription': return 'ðŸ’Š';
      case 'invoice': return 'ðŸ“„';
      default: return 'ðŸ“‹';
    }
  };

  const getTypeColor = (type: string) => {
    switch (type) {
      case 'bill': return 'bg-yellow-100 text-yellow-800';
      case 'prescription': return 'bg-green-100 text-green-800';
      case 'invoice': return 'bg-blue-100 text-blue-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="flex flex-col h-screen bg-background">
      {/* Header */}
      <header className="flex items-center justify-between p-4 border-b border-border">
        <div>
          <h1 className="text-2xl font-bold text-foreground">AI-Powered Reports</h1>
          <p className="text-sm text-muted-foreground">Document analysis and business insights</p>
        </div>
        <Button 
          onClick={() => generateAIReport('summary')}
          disabled={isGeneratingReport}
          className="ml-4"
          data-testid="generate-report-button"
        >
          <Sparkles className="w-4 h-4 mr-2" />
          {isGeneratingReport ? 'Generating...' : 'Generate Report'}
        </Button>
      </header>

      <div className="flex-1 p-4 space-y-6 overflow-y-auto">
        {/* Stats Overview */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          <Card>
            <CardContent className="p-4 text-center">
              <div className="text-2xl font-bold text-primary">{stats.totalDocuments}</div>
              <div className="text-sm text-muted-foreground flex items-center justify-center">
                <FileText className="w-4 h-4 mr-1" />
                Total Documents
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4 text-center">
              <div className="text-2xl font-bold text-blue-600">{stats.documentsByType.bill || 0}</div>
              <div className="text-sm text-muted-foreground">ðŸ§¾ Bills</div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4 text-center">
              <div className="text-2xl font-bold text-green-600">{stats.documentsByType.prescription || 0}</div>
              <div className="text-sm text-muted-foreground">ðŸ’Š Prescriptions</div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4 text-center">
              <div className="text-2xl font-bold text-yellow-600">{stats.documentsByType.invoice || 0}</div>
              <div className="text-sm text-muted-foreground">ðŸ“„ Invoices</div>
            </CardContent>
          </Card>
        </div>

        {/* AI Report Generation */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <Sparkles className="w-5 h-5 mr-2" />
              AI-Generated Reports
            </CardTitle>
            <CardDescription>
              Generate intelligent reports and insights from your document data
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="flex flex-wrap gap-2 mb-4">
              <Button 
                onClick={() => generateAIReport('summary')}
                disabled={isGeneratingReport}
                variant="outline"
                size="sm"
                data-testid="report-summary"
              >
                <BarChart3 className="w-4 h-4 mr-2" />
                Summary Report
              </Button>
              <Button 
                onClick={() => generateAIReport('analysis')}
                disabled={isGeneratingReport}
                variant="outline"
                size="sm"
                data-testid="report-analysis"
              >
                <Search className="w-4 h-4 mr-2" />
                Business Analysis
              </Button>
              <Button 
                onClick={() => generateAIReport('insights')}
                disabled={isGeneratingReport}
                variant="outline"
                size="sm"
                data-testid="report-insights"
              >
                <Calendar className="w-4 h-4 mr-2" />
                Strategic Insights
              </Button>
            </div>

            {/* Display AI Reports */}
            {aiReports.length > 0 && (
              <div className="space-y-4">
                <h4 className="font-medium">Generated Reports</h4>
                {aiReports.map(report => (
                  <Card key={report.id} className="border-l-4 border-l-primary">
                    <CardHeader className="pb-3">
                      <div className="flex items-center justify-between">
                        <CardTitle className="text-base">{report.title}</CardTitle>
                        <Badge variant={
                          report.type === 'summary' ? 'default' :
                          report.type === 'analysis' ? 'secondary' : 'outline'
                        }>
                          {report.type}
                        </Badge>
                      </div>
                      <CardDescription>{formatDate(report.timestamp)}</CardDescription>
                    </CardHeader>
                    <CardContent>
                      <div className="prose prose-sm max-w-none">
                        <pre className="whitespace-pre-wrap text-sm">{report.content}</pre>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Document Search and Filter */}
        <Card>
          <CardHeader>
            <CardTitle>Document Management</CardTitle>
            <CardDescription>Search and manage analyzed documents</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="flex gap-4 mb-4">
              <div className="flex-1">
                <Input
                  placeholder="Search documents..."
                  value={searchText}
                  onChange={(e) => setSearchText(e.target.value)}
                  className="w-full"
                  data-testid="search-documents"
                />
              </div>
              <Select value={selectedType} onValueChange={setSelectedType}>
                <SelectTrigger className="w-[180px]" data-testid="filter-type">
                  <SelectValue placeholder="All Types" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="bill">Bills</SelectItem>
                  <SelectItem value="prescription">Prescriptions</SelectItem>
                  <SelectItem value="invoice">Invoices</SelectItem>
                  <SelectItem value="other">Other</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {/* Documents List */}
            {isLoading ? (
              <div className="text-center py-8">
                <div className="text-muted-foreground">Loading documents...</div>
              </div>
            ) : documents.length === 0 ? (
              <div className="text-center py-8">
                <FileText className="w-12 h-12 mx-auto mb-4 text-muted-foreground" />
                <h3 className="text-lg font-medium mb-2">No documents found</h3>
                <p className="text-muted-foreground">
                  {searchText || selectedType !== 'all' 
                    ? 'Try adjusting your search or filter criteria'
                    : 'Start capturing documents with the Smart Action button'
                  }
                </p>
              </div>
            ) : (
              <div className="space-y-3">
                {documents.map(doc => (
                  <Card key={doc.id} className="hover:shadow-md transition-shadow">
                    <CardContent className="p-4">
                      <div className="flex items-start justify-between">
                        <div className="flex items-center space-x-3 flex-1">
                          <div className="text-2xl">{getTypeIcon(doc.type)}</div>
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center space-x-2 mb-1">
                              <Badge className={getTypeColor(doc.type)}>
                                {doc.type}
                              </Badge>
                              <span className="text-sm text-muted-foreground">
                                {formatDate(doc.createdAt)}
                              </span>
                              {!doc.synced && (
                                <Badge variant="outline" className="text-xs">Offline</Badge>
                              )}
                            </div>
                            <h4 className="font-medium text-sm truncate">{doc.originalFileName}</h4>
                            <p className="text-xs text-muted-foreground mt-1 line-clamp-2">
                              {doc.analysis.rawText.substring(0, 150)}...
                            </p>
                            {doc.analysis.extractedData.medicines && doc.analysis.extractedData.medicines.length > 0 && (
                              <div className="mt-2">
                                <span className="text-xs text-muted-foreground">Medicines: </span>
                                <span className="text-xs">
                                  {doc.analysis.extractedData.medicines.slice(0, 3).map(m => m.name).join(', ')}
                                  {doc.analysis.extractedData.medicines.length > 3 && ` +${doc.analysis.extractedData.medicines.length - 3} more`}
                                </span>
                              </div>
                            )}
                          </div>
                        </div>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => deleteDocument(doc.id)}
                          className="text-destructive hover:text-destructive"
                          data-testid={`delete-document-${doc.id}`}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Storage Stats */}
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between text-sm">
              <span className="text-muted-foreground">Storage Used:</span>
              <span className="font-medium">{formatFileSize(stats.totalSize)}</span>
            </div>
            {stats.unsyncedCount > 0 && (
              <div className="flex items-center justify-between text-sm mt-2">
                <span className="text-muted-foreground">Unsynced Documents:</span>
                <Badge variant="outline">{stats.unsyncedCount}</Badge>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/components/ai-assistant-dialo.content:
import { useState, useEffect, useRef } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent } from '@/components/ui/card';
import { remoteAI, voiceAssistant, ocrAssistant, type AIAssistantContext } from '@/lib/ai-assistant';
import { documentStorage } from '@/services/document-storage';
import { enhancedCapture } from '@/services/enhanced-capture';
import { useToast } from '@/hooks/use-toast';

interface Message {
  id: string;
  role: 'user' | 'assistant';
  content: string;
  timestamp: number;
}

interface AIAssistantDialogProps {
  isOpen: boolean;
  onClose: () => void;
  context?: AIAssistantContext;
}

export function AIAssistantDialog({ isOpen, onClose, context }: AIAssistantDialogProps) {
  const { toast } = useToast();
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputText, setInputText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [voiceSupported, setVoiceSupported] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    checkVoiceSupport();
    if (isOpen && messages.length === 0) {
      addWelcomeMessage();
    }
  }, [isOpen]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const checkVoiceSupport = async () => {
    const supported = await voiceAssistant.isSupported();
    setVoiceSupported(supported);
  };

  const addWelcomeMessage = () => {
    const welcomeMessage: Message = {
      id: crypto.randomUUID(),
      role: 'assistant',
      content: `Hello! I'm your AI assistant for AushadiExpress. I can help you with:\n\nâ€¢ Medicine information and drug interactions\nâ€¢ Inventory management\nâ€¢ Prescription validation\nâ€¢ Document analysis\nâ€¢ Sales insights\n\nHow can I assist you today?`,
      timestamp: Date.now()
    };
    setMessages([welcomeMessage]);
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const addMessage = (role: 'user' | 'assistant', content: string) => {
    const message: Message = {
      id: crypto.randomUUID(),
      role,
      content,
      timestamp: Date.now()
    };
    setMessages(prev => [...prev, message]);
  };

  const handleSendMessage = async (message: string) => {
    if (!message.trim()) return;

    addMessage('user', message);
    setIsLoading(true);

    try {
      // Enhance context with document data for AI queries
      const enhancedContext = await enhanceContextWithDocuments(message, context);
      const response = await remoteAI.ask(message, enhancedContext);
      addMessage('assistant', response);
    } catch (error) {
      console.error('AI Assistant error:', error);
      addMessage('assistant', 'Sorry, I encountered an error. Please try again.');
      toast({
        title: 'Assistant Error',
        description: 'Failed to get AI response',
        variant: 'destructive'
      });
    } finally {
      setIsLoading(false);
    }
  };

  const enhanceContextWithDocuments = async (message: string, currentContext?: AIAssistantContext) => {
    try {
      // Get recent documents for context
      const recentDocs = await enhancedCapture.getRecentDocuments(5);

      // Search for relevant documents based on the message
      const relevantDocs = message.toLowerCase().includes('document') || message.toLowerCase().includes('recent')
        ? await enhancedCapture.searchDocuments(message, undefined)
        : [];

      return {
        ...currentContext,
        userData: {
          ...currentContext?.userData,
          recentDocuments: recentDocs.map(doc => ({
            id: doc.id,
            type: doc.type,
            date: doc.createdAt,
            medicines: doc.analysis.extractedData.medicines || [],
            summary: doc.analysis.rawText.substring(0, 200)
          })),
          relevantDocuments: relevantDocs.slice(0, 3).map(doc => ({
            type: doc.type,
            medicines: doc.analysis.extractedData.medicines || [],
            total: doc.analysis.extractedData.total
          }))
        }
      };
    } catch (error) {
      console.warn('Failed to enhance context with documents:', error);
      return currentContext;
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const message = inputText;
    setInputText('');
    await handleSendMessage(message);
  };

  const handleVoiceInput = async () => {
    if (isListening) {
      await voiceAssistant.stopListening();
      setIsListening(false);
      return;
    }

    try {
      setIsListening(true);
      await voiceAssistant.startListening(
        async (transcript) => {
          setIsListening(false);
          setInputText(transcript);
          // Auto-send voice input
          await handleSendMessage(transcript);
        },
        (error) => {
          setIsListening(false);
          console.error('Voice input error:', error);
          toast({
            title: 'Voice Input Failed',
            description: error === 'not-allowed' ? 'Microphone permission required' : 'Voice recognition failed',
            variant: 'destructive'
          });
        }
      );
    } catch (error) {
      setIsListening(false);
      toast({
        title: 'Voice Not Supported',
        description: 'Voice input is not available on this device',
        variant: 'destructive'
      });
    }
  };

  const handleImageUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    setIsLoading(true);
    addMessage('user', `ðŸ“· Analyzing image: ${file.name}`);

    try {
      const extractedText = await ocrAssistant.scanText(file);

      if (extractedText.trim()) {
        const analysisPrompt = `Please analyze this document text and provide insights:\n\n${extractedText}`;
        const analysis = await remoteAI.ask(analysisPrompt, context);
        addMessage('assistant', `ðŸ“„ **Document Analysis:**\n\n${analysis}\n\n**Extracted Text:**\n${extractedText.substring(0, 500)}${extractedText.length > 500 ? '...' : ''}`);
      } else {
        addMessage('assistant', 'I couldn\'t extract any readable text from this image. Please try a clearer image or different document.');
      }
    } catch (error) {
      console.error('Image analysis error:', error);
      addMessage('assistant', 'Sorry, I couldn\'t analyze that image. Please try again with a clearer photo.');
      toast({
        title: 'Image Analysis Failed',
        description: 'Could not process the uploaded image',
        variant: 'destructive'
      });
    } finally {
      setIsLoading(false);
      // Clear the file input
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  const quickActions = [
    { label: 'Check Drug Interactions', action: () => handleSendMessage('Help me check for drug interactions') },
    { label: 'Recent Documents', action: () => handleSendMessage('Show me recent documents and their analysis') },
    { label: 'Sales Summary', action: () => handleSendMessage('Give me today\'s sales summary') },
    { label: 'Document Report', action: () => handleSendMessage('Generate a report of analyzed documents') }
  ];

  return (
    <>
      <Dialog open={isOpen} onOpenChange={onClose}>
        <DialogContent className="max-w-2xl h-[80vh] flex flex-col">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              ðŸ¤– AI Assistant
              {context?.currentScreen && (
                <Badge variant="secondary" className="text-xs">
                  {context.currentScreen}
                </Badge>
              )}
            </DialogTitle>
          </DialogHeader>

          {/* Messages Area */}
          <div className="flex-1 overflow-y-auto p-4 space-y-4">
            {messages.map((message) => (
              <div key={message.id} className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}>
                <Card className={`max-w-[80%] ${message.role === 'user' ? 'bg-primary text-primary-foreground' : 'bg-muted'}`}>
                  <CardContent className="p-3">
                    <div className="whitespace-pre-wrap text-sm">{message.content}</div>
                    <div className="text-xs opacity-70 mt-1">
                      {new Date(message.timestamp).toLocaleTimeString()}
                    </div>
                  </CardContent>
                </Card>
              </div>
            ))}

            {isLoading && (
              <div className="flex justify-start">
                <Card className="bg-muted">
                  <CardContent className="p-3">
                    <div className="flex items-center gap-2">
                      <div className="w-2 h-2 bg-primary rounded-full animate-pulse"></div>
                      <div className="w-2 h-2 bg-primary rounded-full animate-pulse delay-100"></div>
                      <div className="w-2 h-2 bg-primary rounded-full animate-pulse delay-200"></div>
                      <span className="text-sm text-muted-foreground ml-2">Thinking...</span>
                    </div>
                  </CardContent>
                </Card>
              </div>
            )}
            <div ref={messagesEndRef} />
          </div>

          {/* Quick Actions */}
          {messages.length <= 1 && (
            <div className="px-4 pb-2">
              <div className="text-sm text-muted-foreground mb-2">Quick actions:</div>
              <div className="flex flex-wrap gap-2">
                {quickActions.map((action, index) => (
                  <Button
                    key={index}
                    variant="outline"
                    size="sm"
                    onClick={action.action}
                    disabled={isLoading}
                    className="text-xs"
                  >
                    {action.label}
                  </Button>
                ))}
              </div>
            </div>
          )}

          {/* Input Area */}
          <div className="border-t p-4">
            <form onSubmit={handleSubmit} className="space-y-3">
              <div className="flex gap-2">
                <Input
                  value={inputText}
                  onChange={(e) => setInputText(e.target.value)}
                  placeholder="Ask me anything about pharmacy operations..."
                  disabled={isLoading || isListening}
                  className="flex-1"
                  data-testid="ai-input"
                />
                <Button
                  type="submit"
                  disabled={isLoading || !inputText.trim() || isListening}
                  data-testid="ai-send"
                >
                  <span className="material-icons">send</span>
                </Button>
              </div>

              <div className="flex gap-2">
                {voiceSupported && (
                  <Button
                    type="button"
                    variant={isListening ? "destructive" : "outline"}
                    size="sm"
                    onClick={handleVoiceInput}
                    disabled={isLoading}
                    data-testid="ai-voice"
                  >
                    <span className="material-icons">
                      {isListening ? 'mic_off' : 'mic'}
                    </span>
                    {isListening ? 'Stop' : 'Voice'}
                  </Button>
                )}

                <Button
                  type="button"
                  variant="outline"
                  size="sm"
                  onClick={() => fileInputRef.current?.click()}
                  disabled={isLoading}
                  data-testid="ai-image"
                >
                  <span className="material-icons">photo_camera</span>
                  Analyze Image
                </Button>
              </div>
            </form>
          </div>
        </DialogContent>
      </Dialog>

      {/* Hidden file input */}
      <input
        ref={fileInputRef}
        type="file"
        accept="image/*"
        onChange={handleImageUpload}
        className="hidden"
      />
    </>
  );
}

