import { useState, useRef, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent } from '@/components/ui/card';
import { useToast } from '@/hooks/use-toast';
import { ArrowLeft, Send, Upload, Mic, MicOff, Sparkles, Camera, FileText, X, CheckCircle, AlertCircle, ImageIcon, Zap } from 'lucide-react';
import { useLocation } from 'wouter';
import { enhancedCapture } from '@/services/enhanced-capture';
import { cameraCapture } from '@/services/camera-capture';
import { IntelligentPharmacyAgent } from '@/services/intelligent-pharmacy-agent';
import { useQueryClient } from '@tanstack/react-query';

interface Message {
  id: string;
  role: 'user' | 'assistant' | 'system';
  content: string;
  timestamp: Date;
  type?: 'text' | 'image' | 'document' | 'confirmation';
  imageUrl?: string;
  documentInfo?: {
    type: 'bill' | 'prescription' | 'invoice';
    medicines?: string[];
    total?: number;
    confidence?: number;
  };
  pendingConfirmation?: {
    messageId: string;
    aiClassification: string;
    extractedData: any;
    confidence: number;
    imageFile: File;
  };
}

interface DocumentConfirmationProps {
  aiClassification: string;
  extractedData: any;
  confidence: number;
  onConfirm: (userType: string) => void;
  onCancel: () => void;
}

function DocumentConfirmation({ aiClassification, extractedData, confidence, onConfirm, onCancel }: DocumentConfirmationProps) {
  const [selectedType, setSelectedType] = useState(aiClassification);

  const documentTypes = [
    { value: 'bill', label: 'Customer Bill/Receipt', icon: 'ðŸ§¾', description: 'Sale receipt from customer purchase' },
    { value: 'prescription', label: 'Doctor Prescription', icon: 'ðŸ’Š', description: 'Medical prescription from doctor' },
    { value: 'invoice', label: 'Supplier Invoice', icon: 'ðŸ“‹', description: 'Invoice for inventory purchase' },
    { value: 'other', label: 'Other Document', icon: 'ðŸ“„', description: 'Other pharmacy-related document' }
  ];

  return (
    <Card className="w-full max-w-md mx-auto border-2 border-primary">
      <CardContent className="p-6">
        <div className="text-center mb-4">
          <AlertCircle className="w-8 h-8 mx-auto mb-2 text-amber-500" />
          <h3 className="font-semibold mb-1">Confirm Document Type</h3>
          <p className="text-sm text-muted-foreground">
            AI thinks this is a <strong>{aiClassification}</strong> (confidence: {(confidence * 100).toFixed(1)}%)
          </p>
        </div>

        <div className="space-y-2 mb-4">
          {documentTypes.map((type) => (
            <button
              key={type.value}
              className={`w-full p-3 text-left rounded-lg border transition-colors ${
                selectedType === type.value 
                  ? 'border-primary bg-primary/10' 
                  : 'border-border hover:bg-muted/50'
              }`}
              onClick={() => setSelectedType(type.value)}
            >
              <div className="flex items-center space-x-3">
                <span className="text-xl">{type.icon}</span>
                <div className="flex-1">
                  <div className="font-medium text-sm">{type.label}</div>
                  <div className="text-xs text-muted-foreground">{type.description}</div>
                </div>
                {selectedType === type.value && (
                  <CheckCircle className="w-4 h-4 text-primary" />
                )}
              </div>
            </button>
          ))}
        </div>

        <div className="flex space-x-2">
          <Button variant="outline" className="flex-1" onClick={onCancel}>
            Cancel
          </Button>
          <Button className="flex-1" onClick={() => onConfirm(selectedType)}>
            Confirm
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

export default function AIAssistantFullscreenPage() {
  const [, setLocation] = useLocation();
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputText, setInputText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isListening, setIsListening] = useState(false);
  const [agent] = useState(() => new IntelligentPharmacyAgent());
  const [isUploading, setIsUploading] = useState(false);
  const [pendingConfirmation, setPendingConfirmation] = useState<any>(null);
  const [sessionId] = useState(() => `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Session persistence key
  const SESSION_STORAGE_KEY = `ai_assistant_session_${sessionId}`;

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Load persisted session on mount
  useEffect(() => {
    const loadPersistedSession = () => {
      try {
        // Check for existing active session in last hour
        const allKeys = Object.keys(localStorage);
        const sessionKeys = allKeys.filter(key => key.startsWith('ai_assistant_session_'));

        let loadedSession = null;
        const now = Date.now();
        const ONE_HOUR = 60 * 60 * 1000;

        // Find most recent valid session
        for (const key of sessionKeys) {
          const data = localStorage.getItem(key);
          if (data) {
            try {
              const parsed = JSON.parse(data);
              if (parsed.timestamp && (now - parsed.timestamp) < ONE_HOUR) {
                if (!loadedSession || parsed.timestamp > loadedSession.timestamp) {
                  loadedSession = { key, data: parsed };
                }
              } else {
                // Clean up expired sessions
                localStorage.removeItem(key);
              }
            } catch (e) {
              localStorage.removeItem(key);
            }
          }
        }

        if (loadedSession) {
          console.log('[AI-SESSION] Restoring session from:', loadedSession.key);
          setMessages(loadedSession.data.messages || []);
          return true;
        }

        return false;
      } catch (error) {
        console.error('[AI-SESSION] Error loading session:', error);
        return false;
      }
    };

    // Try to load existing session first
    const sessionLoaded = loadPersistedSession();

    if (!sessionLoaded) {
      // Add welcome message only if no session loaded
      addMessage('assistant', 
        'ðŸ”„ Loading your complete business data for intelligent insights...\n\n' +
        'Welcome to AushadiExpress AI Assistant! ðŸ¥\n\n' +
        'I can help you with:\n' +
        'â€¢ Analyzing bills, prescriptions, and invoices\n' +
        'â€¢ Medicine information and drug interactions\n' +
        'â€¢ Inventory management and stock levels\n' +
        'â€¢ Sales analytics and reporting\n' +
        'â€¢ Business insights from your documents\n\n' +
        'You can upload images, ask questions, or use voice commands. How can I assist you today?'
      );
    }

    // Focus on input after mount
    setTimeout(() => inputRef.current?.focus(), 100);
  }, []);

  // Save session whenever messages change
  useEffect(() => {
    if (messages.length > 0) {
      try {
        const sessionData = {
          messages,
          timestamp: Date.now(),
          sessionId
        };
        localStorage.setItem(SESSION_STORAGE_KEY, JSON.stringify(sessionData));
        console.log('[AI-SESSION] Session saved with', messages.length, 'messages');
      } catch (error) {
        console.error('[AI-SESSION] Error saving session:', error);
      }
    }
  }, [messages, sessionId, SESSION_STORAGE_KEY]);

  const addMessage = (role: 'user' | 'assistant' | 'system', content: string, type: 'text' | 'image' | 'document' | 'confirmation' = 'text', imageUrl?: string, documentInfo?: any, pendingConfirmationData?: any) => {
    const message: Message = {
      id: crypto.randomUUID(),
      role,
      content,
      timestamp: new Date(),
      type,
      imageUrl,
      documentInfo,
      pendingConfirmation: pendingConfirmationData
    };
    setMessages(prev => [...prev, message]);
  };

  // Extract clickable suggestions from message content
  const extractSuggestions = (content: string): string[] => {
    const lines = content.split('\n');
    const suggestions: string[] = [];
    let inSuggestionsSection = false;

    for (const line of lines) {
      // Check for Quick Actions line with pipe-separated format
      if (line.includes('Quick Actions:')) {
        const actionsText = line.split('Quick Actions:')[1]?.trim();
        if (actionsText) {
          const actions = actionsText.split('|').map(action => action.trim()).filter(Boolean);
          suggestions.push(...actions);
        }
        continue;
      }

      // Legacy support for bullet-point suggestions  
      if (line.includes('ðŸ’¡') && (line.includes('Try asking') || line.includes('Try these') || line.includes('asking:'))) {
        inSuggestionsSection = true;
        continue;
      }

      if (inSuggestionsSection) {
        const trimmed = line.trim();
        if (trimmed.startsWith('â€¢ "') && trimmed.endsWith('"')) {
          const suggestion = trimmed.slice(3, -1);
          suggestions.push(suggestion);
        } else if (trimmed.startsWith('â€¢ ')) {
          const suggestion = trimmed.slice(2);
          if (suggestion && !suggestion.includes('**') && suggestion.length > 5) {
            suggestions.push(suggestion);
          }
        } else if (trimmed === '' || (!trimmed.startsWith('â€¢') && trimmed.length > 0 && !trimmed.includes('ðŸ’¡'))) {
          break;
        }
      }
    }

    return suggestions.filter(s => s.length > 2 && s.length < 80);
  };

  // Handle suggestion click
  const handleSuggestionClick = async (suggestion: string) => {
    setInputText('');
    await handleSendMessage(suggestion);
  };

  // Mobile-optimized image compression without quality loss
  const compressImageForMobile = async (file: File): Promise<File> => {
    return new Promise((resolve) => {
      console.log('[MOBILE-DEBUG] Starting image compression', {
        originalSize: file.size,
        originalType: file.type,
        fileName: file.name
      });

      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d')!;
      const img = new Image();

      img.onload = () => {
        // Calculate optimal dimensions (max 1920x1920 for mobile compatibility)
        const maxDimension = 1920;
        let { width, height } = img;

        if (width > maxDimension || height > maxDimension) {
          const ratio = Math.min(maxDimension / width, maxDimension / height);
          width *= ratio;
          height *= ratio;
        }

        canvas.width = width;
        canvas.height = height;

        // Use high-quality settings for compression without quality loss
        ctx.imageSmoothingEnabled = true;
        ctx.imageSmoothingQuality = 'high';
        ctx.drawImage(img, 0, 0, width, height);

        // Convert to blob with optimal quality (0.92 maintains quality while reducing size)
        canvas.toBlob(
          (blob) => {
            if (blob) {
              const compressedFile = new File([blob], file.name, {
                type: file.type,
                lastModified: file.lastModified
              });

              console.log('[MOBILE-DEBUG] Image compression completed', {
                originalSize: file.size,
                compressedSize: compressedFile.size,
                compressionRatio: (compressedFile.size / file.size * 100).toFixed(1) + '%',
                dimensions: `${width}x${height}`
              });

              resolve(compressedFile);
            } else {
              console.warn('[MOBILE-DEBUG] Compression failed, using original');
              resolve(file);
            }
          },
          file.type.includes('png') ? 'image/png' : 'image/jpeg',
          0.92 // High quality compression
        );
      };

      img.onerror = () => {
        console.warn('[MOBILE-DEBUG] Image load failed, using original');
        resolve(file);
      };

      img.src = URL.createObjectURL(file);
    });
  };

  const handleDocumentConfirmation = async (userConfirmedType: string, messageId: string, extractedData: any, imageFile: File) => {
    try {
      setIsLoading(true);
      console.log('[MOBILE-DEBUG] Starting document confirmation storage', {
        userConfirmedType,
        messageId,
        fileName: imageFile.name,
        fileSize: imageFile.size,
        extractedDataKeys: Object.keys(extractedData || {})
      });

      // Store document with user-confirmed type and role metadata
      const userRole = localStorage.getItem('userRole') || 'retailer';
      const isMobile = /Mobi|Android/i.test(navigator.userAgent);

      // Use compressed image for mobile
      const processedFile = isMobile ? await compressImageForMobile(imageFile) : imageFile;

      console.log('[MOBILE-DEBUG] Preparing document storage FormData', {
        originalFile: imageFile.name,
        processedFile: processedFile.name,
        processedSize: processedFile.size,
        isMobileCompressed: isMobile,
        userRole,
        extractedDataSize: JSON.stringify(extractedData).length
      });

      const formData = new FormData();
      formData.append('image', processedFile, processedFile.name);
      formData.append('confirmedType', userConfirmedType);
      formData.append('extractedData', JSON.stringify(extractedData));
      formData.append('userRole', userRole); // Add role for compliance tracking

      // Add mobile-specific debugging headers
      if (isMobile) {
        formData.append('deviceType', 'mobile');
        formData.append('userAgent', navigator.userAgent);
      }

      console.log('[MOBILE-DEBUG] FormData prepared for document storage', {
        userRole,
        confirmedType: userConfirmedType,
        extractedDataLength: JSON.stringify(extractedData).length
      });

      console.log('[MOBILE-DEBUG] Making document storage request to /api/documents');

      const response = await fetch('/api/documents', {
        method: 'POST',
        body: formData,
        credentials: 'same-origin',
        mode: 'cors'
      });

      console.log('[MOBILE-DEBUG] Document storage response received', {
        status: response.status,
        statusText: response.statusText,
        ok: response.ok,
        headers: Object.fromEntries(response.headers.entries())
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error('[MOBILE-DEBUG] Document storage failed - Response text:', errorText);
        console.error('[MOBILE-DEBUG] Document storage failed - Full response:', {
          status: response.status,
          statusText: response.statusText,
          headers: Object.fromEntries(response.headers.entries())
        });
        throw new Error(`Document storage failed: ${response.status} - ${errorText}`);
      }

      const storedDocument = await response.json();

      console.log('[MOBILE-DEBUG] Document stored successfully, invalidating caches');

      // Invalidate React Query caches to refresh all dashboards
      await queryClient.invalidateQueries({ queryKey: ['/api/documents'] });
      await queryClient.invalidateQueries({ queryKey: ['/api/products'] });
      await queryClient.invalidateQueries({ queryKey: ['/api/stock'] });
      await queryClient.invalidateQueries({ queryKey: ['/api/sales'] });
      await queryClient.invalidateQueries({ queryKey: ['/api/sales/today'] });

      console.log('[MOBILE-DEBUG] All caches invalidated for dashboard updates');

      // Remove confirmation message and add success message
      setMessages(prev => prev.filter(m => m.id !== messageId));

      // Count actual medicines from extracted data
      const medicineCount = storedDocument.lineItems?.length || 0;
      const totalAmount = storedDocument.totals?.net || 'N/A';

      addMessage('assistant', 
        `âœ… Document stored successfully!\n\n` +
        `**Type:** ${userConfirmedType.toUpperCase()}\n` +
        `**Role:** ${(localStorage.getItem('userRole') || 'retailer').charAt(0).toUpperCase() + (localStorage.getItem('userRole') || 'retailer').slice(1)}\n` +
        `**ID:** ${storedDocument.id}\n` +
        `**Medicines:** ${medicineCount} items extracted\n` +
        `**Total:** â‚¹${totalAmount}\n\n` +
        `Document has been processed with compliance metadata and is now available for querying.`,
        'document',
        undefined,
        {
          type: userConfirmedType,
          medicines: storedDocument.lineItems?.map((item: any) => item.name) || [],
          total: totalAmount,
          medicineCount: medicineCount
        }
      );

      // Update inventory if it's an invoice with actual line items
      if (userConfirmedType === 'invoice' && storedDocument.lineItems?.length > 0) {
        try {
          // Check for duplicate invoices before updating stock
          const billNumber = storedDocument.header?.docNo;
          const invoiceDate = storedDocument.header?.date;

          if (billNumber) {
            // Enhanced duplicate prevention with date matching
            try {
              const duplicateCheckResponse = await fetch('/api/documents');
              const existingDocs = await duplicateCheckResponse.json();

              const duplicateDoc = existingDocs.find((doc: any) => {
                const sameNumber = doc.header?.docNo === billNumber;
                const sameDate = doc.header?.date === invoiceDate;
                const differentId = doc.id !== storedDocument.id;
                return sameNumber && sameDate && differentId;
              });

              if (duplicateDoc) {
                const dupDate = duplicateDoc.header?.date || 'Unknown date';
                addMessage('system', 
                  `âš ï¸ **Duplicate Invoice Detected**\n\n` +
                  `ðŸ“‹ Invoice: ${billNumber}\n` +
                  `ðŸ“… Date: ${dupDate}\n\n` +
                  `This invoice was already processed. Stock levels remain unchanged to prevent duplication.\n\n` +
                  `ðŸ’¡ You can still ask questions about this document.`
                );
                return; // Skip stock update
              }
            } catch (dupError) {
              console.warn('Duplicate check failed:', dupError);
              // Continue with stock update if duplicate check fails
            }
          }

          const stockResponse = await fetch('/api/stock/bulk', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              items: storedDocument.lineItems.map((item: any) => ({
                name: item.name,
                batch: item.batch || 'N/A',
                qty: parseFloat(item.qty) || 0,
                exp: item.expiry || new Date().toISOString(),
                mrp: parseFloat(item.mrp) || 0
              })),
              billNumber: billNumber,
              date: invoiceDate
            })
          });

          const stockResult = await stockResponse.json();

          if (stockResult.itemsProcessed > 0) {
            addMessage('system', 
              `ðŸ“¦ ${stockResult.message}`
            );
          }
        } catch (error) {
          console.error('Failed to update inventory:', error);
        }
      }

      setPendingConfirmation(null);

    } catch (error) {
      console.error('[MOBILE-DEBUG] Document confirmation failed - Full error:', {
        message: error instanceof Error ? error.message : 'Unknown error',
        stack: error instanceof Error ? error.stack : 'No stack trace',
        name: error instanceof Error ? error.name : 'Unknown error type',
        cause: error instanceof Error ? error.cause : undefined
      });

      let userMessage = 'Could not store the document';
      if (error instanceof Error) {
        if (error.message.includes('Failed to fetch') || error.message.includes('network')) {
          userMessage = 'Network error - check your connection';
        } else if (error.message.includes('413')) {
          userMessage = 'Image file too large - try a smaller image';
        } else if (error.message.includes('400')) {
          userMessage = 'Invalid document format - try a clearer image';
        } else {
          userMessage = `Storage error: ${error.message}`;
        }
      }

      addMessage('assistant', 
        `âŒ **Document Storage Failed**\n\n${userMessage}\n\nðŸ”§ **Debug Info:**\n- Error: ${error instanceof Error ? error.message : 'Unknown'}\n- File: ${imageFile.name}\n- Size: ${Math.round(imageFile.size / 1024)}KB\n\nPlease try again or contact support if this persists.`
      );

      toast({
        title: 'Storage Failed',
        description: userMessage,
        variant: 'destructive'
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleSendMessage = async (message: string, imageFile?: File) => {
    if (!message.trim() && !imageFile) return;

    let userContent = message.trim();

    // Add user message
    if (imageFile) {
      console.log('[MOBILE-DEBUG] Starting image upload process', {
        fileName: imageFile.name,
        fileSize: imageFile.size,
        fileType: imageFile.type,
        userAgent: navigator.userAgent,
        isMobile: /Mobi|Android/i.test(navigator.userAgent)
      });

      const imageUrl = URL.createObjectURL(imageFile);
      addMessage('user', userContent || 'Uploaded an image', 'image', imageUrl);

      // Process the image through AI
      try {
        setIsLoading(true);

        // Compress image for mobile compatibility
        const isMobile = /Mobi|Android/i.test(navigator.userAgent);
        const processedFile = isMobile ? await compressImageForMobile(imageFile) : imageFile;

        console.log('[MOBILE-DEBUG] Creating FormData for upload', {
          originalFile: imageFile.name,
          processedSize: processedFile.size,
          isMobileCompressed: isMobile
        });

        const formData = new FormData();
        formData.append('image', processedFile, processedFile.name);

        // Log FormData contents for mobile debugging
        console.log('[MOBILE-DEBUG] FormData created, sending request to /api/ai/analyze-document');
        console.log('[MOBILE-DEBUG] Request headers will be set automatically by browser');

        const response = await fetch('/api/ai/analyze-document', {
          method: 'POST',
          body: formData,
          // Add credentials and origin handling for mobile compatibility
          credentials: 'same-origin',
          mode: 'cors'
        });

        console.log('[MOBILE-DEBUG] Response received', {
          status: response.status,
          statusText: response.statusText,
          ok: response.ok,
          headers: Object.fromEntries(response.headers.entries())
        });

        if (!response.ok) {
          const errorText = await response.text();
          console.error('[MOBILE-DEBUG] Response not OK, error text:', errorText);
          let errorData;
          try {
            errorData = JSON.parse(errorText);
          } catch {
            errorData = { details: errorText };
          }
          throw new Error(errorData.details || `HTTP ${response.status}: ${response.statusText}`);
        }

        const analysisResult = await response.json();
        console.log('[MOBILE-DEBUG] Analysis result received', {
          documentType: analysisResult.documentType,
          confidence: analysisResult.confidence,
          hasRawText: !!analysisResult.rawText,
          extractedDataKeys: Object.keys(analysisResult.extractedData || {})
        });

        // Show confirmation dialog for document type
        const confirmationId = crypto.randomUUID();
        addMessage('system', 
          'Please confirm the document type to ensure accurate processing:', 
          'confirmation',
          undefined,
          undefined,
          {
            messageId: confirmationId,
            aiClassification: analysisResult.documentType,
            extractedData: analysisResult, // Pass FULL analysis result, not just nested extractedData
            confidence: analysisResult.confidence,
            imageFile
          }
        );

        setPendingConfirmation({
          messageId: confirmationId,
          aiClassification: analysisResult.documentType,
          extractedData: analysisResult, // Pass FULL analysis result, not just nested extractedData
          confidence: analysisResult.confidence,
          imageFile
        });

      } catch (error) {
        console.error('[MOBILE-DEBUG] Image analysis failed:', error);
        console.error('[MOBILE-DEBUG] Error details:', {
          message: error instanceof Error ? error.message : 'Unknown error',
          stack: error instanceof Error ? error.stack : 'No stack trace',
          name: error instanceof Error ? error.name : 'Unknown error type'
        });

        // Enhanced error handling with user-friendly messages
        let userFriendlyMessage = 'âŒ Sorry, I couldn\'t analyze the image.';

        if (error instanceof Error) {
          if (error.message.includes('Failed to fetch') || error.message.includes('network')) {
            userFriendlyMessage = 'ðŸŒ **Network Issue**: Please check your internet connection and try again.';
          } else if (error.message.includes('timeout')) {
            userFriendlyMessage = 'â±ï¸ **Upload Timeout**: The image is taking too long to process. Try a smaller image or check your connection.';
          } else if (error.message.includes('413') || error.message.includes('too large')) {
            userFriendlyMessage = 'ðŸ“ **File Too Large**: Please try a smaller image (under 5MB).';
          } else if (error.message.includes('400') || error.message.includes('bad request')) {
            userFriendlyMessage = 'ðŸ“· **Invalid Image**: Please upload a clear photo of your document.';
          } else {
            userFriendlyMessage = `âŒ **Upload Error**: ${error.message}`;
          }
        }

        addMessage('assistant', 
          `${userFriendlyMessage}\n\nðŸ’¡ **Tips:**\nâ€¢ Ensure good lighting and clear text\nâ€¢ Keep the document flat and in focus\nâ€¢ Try uploading a different image\n\nPlease try again or ask me something else.`
        );
      } finally {
        setIsLoading(false);
      }
    } else {
      addMessage('user', userContent);
    }

    // Get AI response for text queries
    if (userContent && !imageFile) {
      setIsLoading(true);

      try {
        const response = await agent.processQuery(userContent, {
          hasImage: false,
          currentScreen: 'AI Assistant',
          sessionId: sessionId
        });

        addMessage('assistant', response);

      } catch (error) {
        console.error('AI Assistant error:', error);

        // Enhanced error handling for AI queries
        let errorMessage = 'âŒ I encountered an error processing your request.';

        if (error instanceof Error) {
          if (error.message.includes('Failed to fetch') || error.message.includes('network')) {
            errorMessage = 'ðŸŒ **Connection Issue**: Unable to reach the AI service. Please check your internet connection.';
          } else if (error.message.includes('timeout')) {
            errorMessage = 'â±ï¸ **Request Timeout**: The AI is taking too long to respond. Please try a simpler question.';
          } else if (error.message.includes('500')) {
            errorMessage = 'ðŸ”§ **Server Error**: Our AI service is temporarily unavailable. Please try again in a moment.';
          }
        }

        addMessage('assistant', 
          `${errorMessage}\n\nðŸ’¡ **You can try:**\nâ€¢ Rephrasing your question\nâ€¢ Asking about stored documents\nâ€¢ Uploading a new image\nâ€¢ Using voice input`
        );

        toast({
          title: 'Assistant Unavailable',
          description: 'Please try again or contact support if this persists',
          variant: 'destructive'
        });
      } finally {
        setIsLoading(false);
      }
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    const message = inputText;
    setInputText('');
    await handleSendMessage(message);
    inputRef.current?.focus();
  };

  const handleImageUpload = () => {
    fileInputRef.current?.click();
  };

  const handleFileSelect = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      console.log('[MOBILE-DEBUG] File selected from input', {
        fileName: file.name,
        fileSize: file.size,
        fileType: file.type,
        isMobile: /Mobi|Android/i.test(navigator.userAgent)
      });
      await handleSendMessage('', file);
    }
    // Reset file input
    e.target.value = '';
  };

  const handleCameraCapture = async () => {
    try {
      setIsUploading(true);
      console.log('[MOBILE-DEBUG] Camera capture initiated');

      const result = await cameraCapture.captureImage();

      if (result.success && result.file) {
        console.log('[MOBILE-DEBUG] Camera capture successful', {
          fileName: result.file.name,
          fileSize: result.file.size,
          fileType: result.file.type
        });
        await handleSendMessage('', result.file);
      } else {
        console.error('[MOBILE-DEBUG] Camera capture failed:', result.error);
        toast({
          title: 'Capture Failed',
          description: result.error || 'Could not capture image',
          variant: 'destructive'
        });
      }
    } catch (error) {
      console.error('[MOBILE-DEBUG] Camera capture error:', error);
      toast({
        title: 'Capture Failed',
        description: 'Could not access camera',
        variant: 'destructive'
      });
    } finally {
      setIsUploading(false);
    }
  };

  const handleAnalyzeOnly = async (imageFile: File) => {
    console.log('[MOBILE-DEBUG] Starting analyze-only mode', {
      fileName: imageFile.name,
      fileSize: imageFile.size,
      fileType: imageFile.type
    });

    const imageUrl = URL.createObjectURL(imageFile);
    addMessage('user', 'Analyze this image', 'image', imageUrl);

    try {
      setIsLoading(true);

      const formData = new FormData();
      formData.append('image', imageFile);

      console.log('[MOBILE-DEBUG] Sending analyze-only request to /api/ai/analyze-document');

      const response = await fetch('/api/ai/analyze-document', {
        method: 'POST',
        body: formData
      });

      console.log('[MOBILE-DEBUG] Analyze-only response received', {
        status: response.status,
        ok: response.ok
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error('[MOBILE-DEBUG] Analyze-only failed:', errorText);
        let errorData;
        try {
          errorData = JSON.parse(errorText);
        } catch {
          errorData = { details: errorText };
        }
        throw new Error(errorData.details || 'Analysis failed');
      }

      const analysisResult = await response.json();

      // Show analysis results without storing
      let analysisText = `ðŸ“„ **Document Analysis Results**\n\n`;
      analysisText += `**Type:** ${analysisResult.documentType.toUpperCase()}\n`;

      if (analysisResult.extractedData.rawText) {
        analysisText += `**Text Found:** ${analysisResult.extractedData.rawText.substring(0, 300)}${analysisResult.extractedData.rawText.length > 300 ? '...' : ''}\n\n`;
      }

      if (analysisResult.extractedData.header && Object.keys(analysisResult.extractedData.header).length > 0) {
        analysisText += `**Header Info:**\n`;
        Object.entries(analysisResult.extractedData.header).forEach(([key, value]) => {
          if (value) analysisText += `â€¢ ${key}: ${value}\n`;
        });
        analysisText += '\n';
      }

      if (analysisResult.extractedData.lineItems && analysisResult.extractedData.lineItems.length > 0) {
        analysisText += `**Medicines Found (${analysisResult.extractedData.lineItems.length}):**\n`;
        analysisResult.extractedData.lineItems.slice(0, 5).forEach((item: any, index: number) => {
          analysisText += `${index + 1}. ${item.name || 'Unknown'}`;
          if (item.qty) analysisText += ` - Qty: ${item.qty}`;
          if (item.rate) analysisText += ` - Rate: â‚¹${item.rate}`;
          analysisText += '\n';
        });
        if (analysisResult.extractedData.lineItems.length > 5) {
          analysisText += `...and ${analysisResult.extractedData.lineItems.length - 5} more items\n`;
        }
        analysisText += '\n';
      }

      if (analysisResult.extractedData.totals && Object.keys(analysisResult.extractedData.totals).length > 0) {
        analysisText += `**Totals:**\n`;
        Object.entries(analysisResult.extractedData.totals).forEach(([key, value]) => {
          if (value) analysisText += `â€¢ ${key}: â‚¹${value}\n`;
        });
      }

      analysisText += '\n*This is analysis only - document was not stored.*';

      addMessage('assistant', analysisText);

    } catch (error) {
      console.error('Image analysis failed:', error);
      addMessage('assistant', 
        `âŒ Analysis failed: ${error instanceof Error ? error.message : 'Unknown error'}\n\nPlease try again.`
      );
    } finally {
      setIsLoading(false);
    }
  };

  const handleAnalyzeImage = async () => {
    if (fileInputRef.current) {
      fileInputRef.current.setAttribute('data-analyze', 'true');
      fileInputRef.current.click();
    }
  };

  const handleVoiceToggle = async () => {
    if (isListening) {
      setIsListening(false);
    } else {
      setIsListening(true);
      toast({
        title: 'Voice Recognition',
        description: 'Voice input will be available in a future update',
        variant: 'default'
      });
      setTimeout(() => setIsListening(false), 2000);
    }
  };

  const quickActions = [
    { 
      label: 'Analyze Medicine Interactions', 
      icon: 'ðŸ’Š',
      action: () => handleSendMessage('Help me check for drug interactions and safety information')
    },
    { 
      label: 'Recent Documents Summary', 
      icon: 'ðŸ“„',
      action: () => handleSendMessage('Show me a summary of recently uploaded documents')
    },
    { 
      label: 'Inventory Status', 
      icon: 'ðŸ“¦',
      action: () => handleSendMessage('What\'s my current inventory status and any low stock alerts?')
    },
    { 
      label: 'Sales Analytics', 
      icon: 'ðŸ“Š',
      action: () => handleSendMessage('Give me today\'s sales analytics and key insights')
    }
  ];

  const formatTimestamp = (date: Date) => {
    return new Intl.DateTimeFormat('en-US', {
      hour: '2-digit',
      minute: '2-digit'
    }).format(date);
  };

  return (
    <div className="fixed inset-0 z-50 bg-background flex flex-col overflow-hidden" style={{
      height: '100dvh', // Dynamic viewport height for better mobile support
      width: '100vw',
      maxHeight: '-webkit-fill-available'
    }}>
      {/* Header - Fixed height to prevent jumping */}
      <header className="flex items-center justify-between p-4 border-b border-border bg-background/95 backdrop-blur sticky top-0 z-10 shrink-0 h-16">
        <div className="flex items-center space-x-3">
          <Button 
            variant="ghost" 
            size="sm" 
            onClick={() => setLocation('/')}
            data-testid="back-button"
          >
            <X className="w-4 h-4" />
          </Button>
          <div>
            <h1 className="text-lg font-semibold text-foreground flex items-center">
              <Sparkles className="w-5 h-5 mr-2" />
              AI Assistant
            </h1>
            <p className="text-xs text-muted-foreground">Pharmacy Intelligence</p>
          </div>
        </div>
        <Badge variant="secondary" className="text-xs">
          {isLoading ? 'Thinking...' : 'Ready'}
        </Badge>
      </header>

      {/* Messages Area - Prevent jumping with stable height calculations */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4" style={{ 
        minHeight: 0,
        maxHeight: 'calc(100vh - 140px)', // Account for header + input area
        scrollBehavior: 'smooth',
        overscrollBehavior: 'contain'
      }}>
        {messages.length === 0 && !isLoading && (
          <div className="text-center py-8">
            <Sparkles className="w-12 h-12 mx-auto mb-4 text-muted-foreground" />
            <h3 className="text-lg font-medium mb-2">AI Assistant Ready</h3>
            <p className="text-muted-foreground mb-6">
              Upload documents, ask questions, or try a quick action below
            </p>

            {/* Quick Actions */}
            <div className="grid grid-cols-1 gap-3 max-w-md mx-auto">
              {quickActions.map((action, index) => (
                <Button
                  key={index}
                  variant="outline"
                  className="justify-start h-auto py-3 px-4 text-left"
                  onClick={action.action}
                  data-testid={`quick-action-${index}`}
                >
                  <span className="text-lg mr-3">{action.icon}</span>
                  <span className="text-sm">{action.label}</span>
                </Button>
              ))}
            </div>
          </div>
        )}

        {messages.map((message) => (
          <div key={message.id}>
            {message.type === 'confirmation' && message.pendingConfirmation ? (
              <div className="flex justify-center">
                <DocumentConfirmation
                  aiClassification={message.pendingConfirmation.aiClassification}
                  extractedData={message.pendingConfirmation.extractedData}
                  confidence={message.pendingConfirmation?.confidence || 0.5}
                  onConfirm={(userType) => {
                    if (message.pendingConfirmation?.imageFile) {
                      handleDocumentConfirmation(
                        userType, 
                        message.id, 
                        message.pendingConfirmation?.extractedData, 
                        message.pendingConfirmation.imageFile
                      );
                    }
                  }}
                  onCancel={() => {
                    setMessages(prev => prev.filter(m => m.id !== message.id));
                    setPendingConfirmation(null);
                  }}
                />
              </div>
            ) : (
              <div
                className={`flex ${message.role === 'user' ? 'justify-end' : message.role === 'system' ? 'justify-center' : 'justify-start'}`}
              >
                <div
                  className={`max-w-[85%] rounded-2xl px-4 py-3 ${
                    message.role === 'user'
                      ? 'bg-primary text-primary-foreground ml-12'
                      : message.role === 'system'
                      ? 'bg-amber-100 dark:bg-amber-950/20 text-amber-800 dark:text-amber-200 mx-4'
                      : 'bg-muted mr-12'
                  }`}
                >
                  {message.type === 'image' && message.imageUrl && (
                    <div className="mb-2">
                      <img 
                        src={message.imageUrl} 
                        alt="Uploaded image" 
                        className="max-w-full h-auto rounded-lg"
                        style={{ maxHeight: '200px' }}
                      />
                    </div>
                  )}

                  {message.type === 'document' && message.documentInfo && (
                    <div className="mb-2 p-3 bg-background/50 rounded-lg border">
                      <div className="flex items-center space-x-2 mb-2">
                        <FileText className="w-4 h-4" />
                        <Badge variant="outline">{message.documentInfo.type}</Badge>
                        {message.documentInfo.confidence && (
                          <Badge variant="secondary" className="text-xs">
                            {(message.documentInfo.confidence * 100).toFixed(1)}%
                          </Badge>
                        )}
                      </div>
                      {message.documentInfo.medicines && message.documentInfo.medicines.length > 0 && (
                        <div className="text-xs text-muted-foreground">
                          Medicines: {message.documentInfo.medicines.slice(0, 3).join(', ')}
                          {message.documentInfo.medicines.length > 3 && ` +${message.documentInfo.medicines.length - 3} more`}
                        </div>
                      )}
                      {message.documentInfo.total && (
                        <div className="text-xs text-muted-foreground">
                          Total: â‚¹{message.documentInfo.total}
                        </div>
                      )}
                    </div>
                  )}

                  <div className="whitespace-pre-wrap text-sm">
                    {message.content}
                  </div>

                  {/* Clickable Suggestions */}
                  {message.role === 'assistant' && (() => {
                    const suggestions = extractSuggestions(message.content);
                    return suggestions.length > 0 ? (
                      <div className="mt-3 space-y-2">
                        <div className="text-xs text-muted-foreground font-medium">
                          ðŸ’¡ Quick actions:
                        </div>
                        <div className="flex flex-wrap gap-2">
                          {suggestions.slice(0, 4).map((suggestion, index) => (
                            <Button
                              key={index}
                              variant="outline"
                              size="sm"
                              className="h-8 text-xs bg-blue-50 hover:bg-blue-100 border-blue-200 text-blue-700 dark:bg-blue-950/20 dark:hover:bg-blue-950/30 dark:border-blue-800 dark:text-blue-300"
                              onClick={() => handleSuggestionClick(suggestion)}
                              data-testid={`suggestion-button-${index}`}
                            >
                              {suggestion.length > 35 ? `${suggestion.slice(0, 32)}...` : suggestion}
                            </Button>
                          ))}
                        </div>
                      </div>
                    ) : null;
                  })()}

                  <div className={`text-xs mt-2 ${
                    message.role === 'user' ? 'text-primary-foreground/70' : 'text-muted-foreground'
                  }`}>
                    {formatTimestamp(message.timestamp)}
                  </div>
                </div>
              </div>
            )}
          </div>
        ))}

        {isLoading && (
          <div className="flex justify-start">
            <div className="bg-muted rounded-2xl px-4 py-3 mr-12">
              <div className="flex items-center space-x-2">
                <div className="animate-spin w-4 h-4 border-2 border-current border-t-transparent rounded-full"></div>
                <span className="text-sm">AI is thinking...</span>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {/* Input Area - ChatGPT/Perplexity Style with Mobile Optimization */}
      <div className="border-t border-border bg-background p-4 shrink-0" style={{ 
        paddingBottom: 'max(env(safe-area-inset-bottom, 16px), 16px)',
        minHeight: '120px', // Increased for larger buttons
        position: 'sticky',
        bottom: 0,
        zIndex: 10
      }}>
        {/* Larger Action Buttons for Mobile */}
        <div className="flex justify-center space-x-3 mb-4">
          <Button
            variant="ghost"
            size="lg"
            onClick={handleCameraCapture}
            disabled={isUploading || isLoading}
            data-testid="camera-button"
            className="h-12 w-12 p-0 rounded-full hover:bg-muted/50 bg-muted/20 border border-border/50"
            title="Take Photo"
          >
            <Camera className="w-6 h-6 text-foreground" />
          </Button>
          <Button
            variant="ghost"
            size="lg"
            onClick={handleImageUpload}
            disabled={isUploading || isLoading}
            data-testid="upload-button"
            className="h-12 w-12 p-0 rounded-full hover:bg-muted/50 bg-muted/20 border border-border/50"
            title="Upload Image"
          >
            <ImageIcon className="w-6 h-6 text-foreground" />
          </Button>
          <Button
            variant="ghost"
            size="lg"
            onClick={handleAnalyzeImage}
            disabled={isUploading || isLoading}
            data-testid="analyze-button"
            className="h-12 w-12 p-0 rounded-full hover:bg-muted/50 bg-muted/20 border border-border/50"
            title="Analyze Only"
          >
            <Zap className="w-6 h-6 text-foreground" />
          </Button>
          <Button
            variant="ghost"
            size="lg"
            onClick={handleVoiceToggle}
            disabled={isLoading}
            data-testid="voice-button"
            className={`h-12 w-12 p-0 rounded-full hover:bg-muted/50 border border-border/50 ${
              isListening ? 'bg-red-100 dark:bg-red-900/20' : 'bg-muted/20'
            }`}
            title="Voice Input"
          >
            {isListening ? (
              <div className="relative">
                <MicOff className="w-6 h-6 text-red-600" />
                <div className="absolute inset-0 animate-ping">
                  <MicOff className="w-6 h-6 text-red-600 opacity-20" />
                </div>
              </div>
            ) : (
              <Mic className="w-6 h-6 text-foreground" />
            )}
          </Button>
        </div>

        {/* Text Input - ChatGPT Style */}
        <form onSubmit={handleSubmit} className="relative">
          <Input
            ref={inputRef}
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            placeholder="Ask about medicines, documents, or get insights..."
            disabled={isLoading}
            className="flex-1 pr-12 rounded-2xl border-2 resize-none min-h-[44px] text-base bg-background focus:border-primary/50 transition-colors"
            data-testid="message-input"
            style={{ fontSize: '16px' }} // Prevent zoom on iOS
          />
          <Button 
            type="submit" 
            disabled={isLoading || (!inputText.trim())}
            data-testid="send-button"
            className="absolute right-2 top-1/2 transform -translate-y-1/2 h-8 w-8 p-0 rounded-full bg-primary hover:bg-primary/90 disabled:bg-muted disabled:text-muted-foreground"
            size="sm"
          >
            <Send className="w-4 h-4" />
          </Button>
        </form>

        <div className="text-xs text-center text-muted-foreground">
          AI responses may contain errors. Verify important medical information.
        </div>
      </div>

      {/* Hidden file input */}
      <input
        ref={fileInputRef}
        type="file"
        accept="image/*"
        className="hidden"
        onChange={handleFileSelect}
      />
    </div>
  );
}

