              let currentQty = 0;
              if (matchingProduct) {
                currentQty = stock
                  .filter((s: any) => s.productId === matchingProduct.id)
                  .reduce((sum: number, s: any) => sum + s.quantity, 0);
              }

              const currentValue = rate * currentQty;
              totalCurrentValue += currentValue;

              medicineDetails.push({
                name: item.name,
                originalQty,
                currentQty,
                rate,
                originalValue,
                currentValue
